# inverse of test 5 in AES_test.py
def test2_invAES():
    # Input array
    hex_in = "69c4e0d86a7b0430d8cdb78070b4c55a"
    byte_in = bytes.fromhex(hex_in)
    byte_array_in = np.frombuffer(byte_in, dtype=np.byte)

    hex_key = "000102030405060708090a0b0c0d0e0f"
    byte_key = bytes.fromhex(hex_key)
    byte_array_key = np.frombuffer(byte_key, dtype=np.byte)

    # Reference output
    hex_ref = "00112233445566778899aabbccddeeff"
    byte_ref = bytes.fromhex(hex_ref)
    byte_array_ref = np.frombuffer(byte_ref, dtype=np.byte)

    graphicscomputer = InvAESTest()
    result = graphicscomputer.invAES_gpu(byte_array_in, byte_array_key, byte_array_in.size)[0]
    print(len(result))
    print(result)
    print(len(byte_array_ref))
    print(byte_array_ref)
    assert np.array_equal(result, byte_array_ref)

